import React from 'react';
import { motion } from 'framer-motion';
import { tierInfo } from '../../data/achievements';

const AchievementBadge = ({ achievement, size = 'medium', showGlow = true, onClick }) => {
  const tier = tierInfo[achievement.tier];
  const isUnlocked = achievement.unlockedAt;

  const sizes = {
    small: { badge: 48, icon: 16, text: 'text-xs' },
    medium: { badge: 72, icon: 24, text: 'text-sm' },
    large: { badge: 96, icon: 32, text: 'text-base' }
  };

  const currentSize = sizes[size];

  return (
    <motion.div
      className={`relative cursor-pointer ${currentSize.text}`}
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      onClick={onClick}
    >
      {/* Outer glow effect for unlocked badges */}
      {isUnlocked && showGlow && (
        <div 
          className="absolute inset-0 rounded-full blur-sm animate-pulse"
          style={{
            background: `radial-gradient(circle, ${tier.color}40 0%, transparent 70%)`,
            width: currentSize.badge + 20,
            height: currentSize.badge + 20,
            left: -10,
            top: -10
          }}
        />
      )}

      {/* Main badge */}
      <div
        className="relative border-4 rounded-full flex items-center justify-center overflow-hidden"
        style={{
          width: currentSize.badge,
          height: currentSize.badge,
          borderColor: isUnlocked ? tier.color : '#4B5563',
          backgroundColor: isUnlocked ? `${tier.color}20` : '#1F2937',
          boxShadow: isUnlocked 
            ? `0 0 20px ${tier.color}60, inset 0 0 20px ${tier.color}20`
            : 'inset 0 0 10px rgba(0,0,0,0.5)'
        }}
      >
        {/* Gradient overlay for shine effect */}
        {isUnlocked && (
          <div 
            className="absolute inset-0 rounded-full"
            style={{
              background: `linear-gradient(135deg, 
                ${tier.color}30 0%, 
                transparent 30%, 
                transparent 70%, 
                ${tier.color}20 100%)`
            }}
          />
        )}

        {/* Badge content */}
        <div className="relative z-10 text-center">
          {isUnlocked ? (
            <div 
              className="font-mono font-bold"
              style={{ 
                color: tier.color,
                fontSize: currentSize.icon,
                textShadow: `0 0 10px ${tier.color}80`
              }}
            >
              {tier.emoji}
            </div>
          ) : (
            <div className="text-gray-600 font-mono font-bold">
              ðŸ”’
            </div>
          )}
        </div>

        {/* Rotating border animation for legendary badges */}
        {isUnlocked && achievement.tier === 'legendary' && (
          <motion.div
            className="absolute inset-0 rounded-full border-2"
            style={{ borderColor: `${tier.color}80` }}
            animate={{ rotate: 360 }}
            transition={{ duration: 4, repeat: Infinity, ease: "linear" }}
          />
        )}
      </div>

      {/* Badge tier indicator */}
      <div 
        className="absolute -bottom-1 left-1/2 transform -translate-x-1/2 px-2 py-0.5 rounded text-xs font-mono font-bold border"
        style={{
          backgroundColor: isUnlocked ? tier.color : '#4B5563',
          color: isUnlocked ? '#000' : '#9CA3AF',
          borderColor: isUnlocked ? tier.color : '#4B5563'
        }}
      >
        {tier.name.charAt(0)}
      </div>
    </motion.div>
  );
};

export default AchievementBadge;
