import React from 'react';
import { Plus, Trophy } from 'lucide-react';
import AchievementBadge from '../ui/AchievementBadge';

/**
 * Favorite Badges Section Component
 * Displays selected achievement badges and add button
 */
const FavoriteBadgesSection = ({
  selectedBadges,
  onOpenBadgeSelector,
  canAddMoreBadges,
  hasUnlockedAchievements
}) => {
  // Create array of 3 slots, filling with badges or empty slots
  const badgeSlots = Array.from({ length: 3 }, (_, index) => {
    return selectedBadges[index] || null;
  });

  return (
    <div className="mb-8">
      {/* Section Header */}
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-white flex items-center gap-2">
          <Trophy className="text-yellow-400" size={20} />
          Favorite Badges
        </h3>

        {/* Add Badges Button */}
        {hasUnlockedAchievements && (
          <button
            onClick={onOpenBadgeSelector}
            className="flex items-center gap-2 px-4 py-2 bg-black/80 hover:bg-black/90 text-white rounded-lg border border-purple-500/30 hover:border-purple-400/50 transition-all duration-200 text-sm font-medium shadow-lg backdrop-blur-sm"
          >
            <Plus size={16} />
            {selectedBadges.length === 0 ? 'Add Badges' : 'Edit Badges'}
          </button>
        )}
      </div>

      {/* Badge Grid */}
      <div className="grid grid-cols-3 gap-3">
        {badgeSlots.map((badge, index) => (
          <div key={index} className="aspect-[3/1]">
            {badge ? (
              <AchievementBadge 
                achievement={badge}
                variant="horizontal"
                showProgress={false}
                className="h-full w-full"
              />
            ) : (
              <div className="h-full w-full bg-gray-800/30 border-2 border-dashed border-gray-600/50 rounded-lg flex items-center justify-center group hover:border-gray-500/50 transition-colors">
                <div className="text-center">
                  <div className="w-8 h-8 bg-gray-700/50 rounded-full flex items-center justify-center mx-auto mb-1 group-hover:bg-gray-600/50 transition-colors">
                    <Trophy size={16} className="text-gray-500 group-hover:text-gray-400 transition-colors" />
                  </div>
                  <span className="text-xs text-gray-500 group-hover:text-gray-400 transition-colors">
                    Empty Slot
                  </span>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* No achievements message */}
      {!hasUnlockedAchievements && (
        <div className="grid grid-cols-3 gap-3">
          {badgeSlots.map((_, index) => (
            <div key={index} className="aspect-[3/1]">
              <div className="h-full w-full bg-gray-800/20 border-2 border-dashed border-gray-700/30 rounded-lg flex items-center justify-center">
                <div className="text-center">
                  <div className="w-8 h-8 bg-gray-800/30 rounded-full flex items-center justify-center mx-auto mb-1">
                    <Trophy size={16} className="text-gray-600" />
                  </div>
                  <span className="text-xs text-gray-600">
                    Unlock achievements to add badges
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FavoriteBadgesSection;
