import apiService from './api';

class BackendAchievementService {
  constructor() {
    this.allAchievements = [];
    this.playerAchievements = [];
    this.playerStats = {};
    this.loaded = false;
  }

  async loadData(username = 'testuser') {
    try {
      const [achievements, playerAchievements, stats] = await Promise.all([
        apiService.getAllAchievements(),
        apiService.getPlayerAchievements(username),
        apiService.getAchievementPlayerStats(username)
      ]);

      this.allAchievements = achievements;
      this.playerAchievements = playerAchievements;
      this.playerStats = stats;
      this.loaded = true;

      console.log('✅ Backend achievement data loaded:', {
        achievements: achievements.length,
        playerProgress: playerAchievements.length,
        stats
      });

      return { achievements, playerAchievements, stats };
    } catch (error) {
      console.error('❌ Failed to load backend achievements:', error);
      throw error;
    }
  }

  getAchievementProgress(achievementId, userStats) {
    const playerAchievement = this.playerAchievements.find(pa => pa.achievementId === achievementId);
    if (playerAchievement?.completed) return 1;

    return (playerAchievement?.progress || 0) / 100; // Backend stores as percentage, frontend expects decimal
  }

  isUnlocked(achievementId) {
    const playerAchievement = this.playerAchievements.find(pa => 
      (pa.achievementId === achievementId || pa.achievement_id === achievementId)
    );
    return playerAchievement?.completed || false;
  }

  getUnlockedAchievements() {
    return this.playerAchievements
      .filter(pa => pa.completed) // Filter completed player achievements
      .map(playerAch => {
        // Match by achievementId or achievement_id
        const achievementId = playerAch.achievementId || playerAch.achievement_id;
        const fullAchievement = this.allAchievements.find(all => all.id === achievementId);
        return fullAchievement ? {
          ...fullAchievement,
          unlockedAt: playerAch.unlockedAt || playerAch.updatedAt
        } : null;
      })
      .filter(Boolean);
  }

  getLockedAchievements() {
    return this.allAchievements.filter(a => !this.isUnlocked(a.id));
  }

  getInProgressAchievements() {
    return this.playerAchievements
      .filter(pa => !pa.completed && (pa.progress > 0 || pa.currentProgress > 0))
      .map(playerAch => {
        const achievementId = playerAch.achievementId || playerAch.achievement_id;
        const fullAchievement = this.allAchievements.find(all => all.id === achievementId);
        return fullAchievement ? { 
          ...fullAchievement, 
          progress: pa.progress || 0,
          currentProgress: pa.currentProgress || 0,
          targetValue: fullAchievement.targetValue
        } : null;
      })
      .filter(Boolean);
  }

  getStats() {
    const unlocked = this.getUnlockedAchievements();
    const total = this.allAchievements.length;

    return {
      total,
      unlocked: unlocked.length,
      percentage: total > 0 ? Math.round((unlocked.length / total) * 100) : 0,
      totalXP: this.playerStats.totalXp || 0,
      byTier: {
        common: unlocked.filter(a => a.tier === 'common').length,
        uncommon: unlocked.filter(a => a.tier === 'uncommon').length,
        rare: unlocked.filter(a => a.tier === 'rare').length,
        legendary: unlocked.filter(a => a.tier === 'legendary').length
      }
    };
  }
}

export default new BackendAchievementService();
